<?php if ($this->get('entity')->hasStates()): ?><a href="<?php echo func_htmlspecialchars($this->buildURL('states','',array('country_code'=>$this->get('entity')->getCode()))); ?>" class="country-states"  title="<?php echo func_htmlspecialchars($this->t('Click to edit states')); ?>"><?php echo func_htmlspecialchars($this->t('Edit states (X)',array('count'=>$this->get('entity')->getStatesCount()))); ?></a><?php endif; ?>

<?php if (!($this->get('entity')->hasStates())): ?><a href="<?php echo func_htmlspecialchars($this->buildURL('states','',array('country_code'=>$this->get('entity')->getCode()))); ?>" class="country-states no-states"  title="<?php echo func_htmlspecialchars($this->t('Click to add states')); ?>"><?php echo func_htmlspecialchars($this->t('Add states')); ?></a><?php endif; ?>
