<?php if ($this->get('entity')->hasRates()): ?><a href="<?php echo func_htmlspecialchars($this->buildURL('shipping_rates','',array('methodid'=>$this->get('entity')->getMethodId()))); ?>" class="rates"  title="<?php echo func_htmlspecialchars($this->t('Click to edit rates')); ?>"><?php echo func_htmlspecialchars($this->t('Edit rates (X)',array('count'=>$this->get('entity')->getRatesCount()))); ?></a><?php endif; ?>

<?php if (!($this->get('entity')->hasRates())): ?><a href="<?php echo func_htmlspecialchars($this->buildURL('shipping_rates','',array('methodid'=>$this->get('entity')->getMethodId()))); ?>#addmarkup" class="rates no-rates"  title="<?php echo func_htmlspecialchars($this->t('Click to add rates')); ?>"><?php echo func_htmlspecialchars($this->t('Add rates')); ?></a><?php endif; ?>
